mapper.update:
UPDATE score
SET score.score = #{score}
WHERE score.exam_id = #{examId} AND score.user_id = #{studentId} AND score.question_paper_id =#{questionPaperId}

UPDATE score
SET score.score = #{score}
WHERE score.exam_id = #{examId} AND score.user_id = #{studentId} AND score.question_paper_id IS NULL

mapper.select:
SELECT *
FROM score
WHERE score.exam_id = #{examId} AND score.user_id = #{studentId} AND score.question_paper_id  = #{questionPaperId}

SELECT grade
FROM question_paper
WHERE question_paper.paper_id = #{paperId} AND question_paper.question_paper_sort = #{questionPaperId}
